import Drink from 'Components/ScreenBoard/Drink';
import { DRINK_API } from 'Helper/constant';
import { fetchData } from 'Helper/utils';
import { useEffect, useState } from 'react';

export default function ScreenBoard() {
  const [beverages, setBeverages] = useState([]);
  useEffect(() => {
    const getBeverages = async () => {
      const beverageDatas = await fetchData(DRINK_API);
      setBeverages(beverageDatas);
    };
    getBeverages();
  }, []);

  return (
    <>
      {beverages.map(({ drinkId, drinkTitle, drinkPrice }) => {
        return <Drink id={drinkId} title={drinkTitle} price={drinkPrice} />;
      })}
    </>
  );
}
