import styled from 'styled-components';

export const ScreenContainer = styled.div`
  width: 1000px;
  height: 500px;
  border: 2px solid #3f51b5;
  box-sizing: border-box;
`;

export const Screen = styled.div`
  padding: 20px;
`;

export const Message = styled.div`
  font-size: 1.75rem;
  margin-bottom: 10px;
`;
