import { initAlertMessage } from 'Helper/constant';
import { getWonTemplate } from 'Helper/utils';
import { AlertMessage, SetAlertMessage } from 'Pages/VendingMachine';
import { useContext, useEffect, useState } from 'react';
import { Message, Screen, ScreenContainer } from './MessageScreen.styled';

export default function MessageScreen() {
  const alertMessage = useContext(AlertMessage);
  const setAlertMessage = useContext(SetAlertMessage);
  const [messageList, setMessageList] = useState([]);

  useEffect(() => {
    const message = Object.entries(alertMessage).reduce((message, [alertName, alertAmount]) => {
      if (!alertAmount) {
        return message;
      }
      return createAlertMessage(alertName, alertAmount);
    }, '');
    if (!message) {
      return;
    }
    const newMessageList = [...messageList, message];
    setMessageList(newMessageList);
    setAlertMessage(initAlertMessage);
  }, [alertMessage]);

  return (
    <ScreenContainer>
      <Screen>
        {messageList && messageList.map((message, idx) => <Message key={createKey(message, idx)}>{message}</Message>)}
      </Screen>
    </ScreenContainer>
  );
}

const createAlertMessage = (alertName, alertAmount) => {
  const alerts = {
    chargeCash: createInvestmentMessage,
    changeAmount: createChangeMessage,
    orderTitle: createOrderMessage,
  };
  return alerts[alertName](alertAmount);
};

const createOrderMessage = (title) => {
  return `${title}가 주문되었습니다.`;
};

const createChangeMessage = (amount) => {
  return `거스름돈 ${getWonTemplate(amount)}이 반환되었습니다.`;
};

const createInvestmentMessage = (amount) => {
  return `자판기에 ${getWonTemplate(amount)}이 투입되었습니다.`;
};

const createKey = (message, key) => {
  return `${message + key}`;
};
