  const alertList = useContext(AlertList);
  const [messageList, setMessageList] = useState([]);
  useEffect(() => {
    Object.entries(alertList).reduce((message, [alertName, alertAmount]) => {
      createAlertMessage(alertName, alertAmount);
    }, 0);
  }, [alertList]);

  return (
    <ScreenContainer>
      <Screen>
        <Message>야호</Message>
        <Message>야호</Message>
      </Screen>
    </ScreenContainer>
  );
}

