import { applyFlex } from 'Helper/utils';
import styled from 'styled-components';

export const ScreenBoard = styled.div`
  border: 2px solid #3f51b5;
  ${({ flex, wrap }) => applyFlex({ flex, wrap })};
  width: 580px;
`;
